	 and engine_no = #{engineNo}  
	 and brand_modal = #{brandModal}  
	 and brand_modal_ch = #{brandModalCh}  
	 and register_date = #{registerDate}  
	 and is_secondhand = #{isSecondhand}  
	 and transfor_date = #{transforDate}

