AND order_id=#{orderId}

mapper.select:
select
	order_id,thirdpart_order_id,insurance_company,req_branch,compulsory_prop_no,business_prop_no,compulsory_policy_no,business_policy_no,insure_city,car_plate_no,has_plate,total_price,order_status,create_time,update_time
from t_order_list

	 AND order_id = #{orderId}
	 AND thirdpart_order_id = #{thirdpartOrderId}
	 AND insurance_company = #{insuranceCompany}
	 AND req_branch = #{reqBranch}
	 AND compulsory_prop_no = #{compulsoryPropNo}
	 AND business_prop_no = #{businessPropNo}
	 AND compulsory_policy_no = #{compulsoryPolicyNo}
	 AND business_policy_no = #{businessPolicyNo}
	 AND insure_city = #{insureCity}
	 AND car_plate_no = #{carPlateNo}
	 AND has_plate = #{hasPlate}
	 AND total_price = #{totalPrice}
	 AND order_status = #{orderStatus}
	 AND create_time = #{createTime}
	 AND update_time = #{updateTime}
